const navLinks=document.querySelectorAll(".nav--link");window.addEventListener("scroll",()=>{let a=window.scrollY+2;navLinks.forEach(b=>{let c=document.querySelector(b.hash);c.offsetTop-63<=a&&c.offsetTop-63+c.offsetHeight>a?b.classList.add("current"):b.classList.remove("current")})});const footerCopy=document.getElementsByClassName("footer--year"),currentYear=new Date().getFullYear();footerCopy[0].textContent=currentYear;